<template>  <nav class="navbar navbar-expand-lg navbar-dark bg-primary box shadow-lg">    <div class="container-fluid">      <a class="navbar-brand" href="#">Jesus Carros</a>      <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">        <span class="navbar-toggler-icon"></span>      </button>      <div class="collapse navbar-collapse" id="navbarSupportedContent">        <ul class="navbar-nav me-auto mb-2 mb-lg-0">          <li class="nav-item">            <router-link to="/home" class="text-decoration-none"><a class="nav-link active" aria-current="page" href="#">Home</a></router-link>          </li>          <li class="nav-item">            <router-link to="/dashboard" class="text-decoration-none"><a class="nav-link active" aria-current="page" href="#">Dashboard</a></router-link>          </li>          <li class="nav-item">            <router-link to="/create/car" class="text-decoration-none"><a class="nav-link" href="#">Post Car</a></router-link>          </li>          <li class="nav-item dropdown">            <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">              {{ currentUser.name }}            </a>            <ul class="dropdown-menu" aria-labelledby="navbarDropdown">              <li><a class="dropdown-item" href="#">My Cars</a></li>              <li><hr class="dropdown-divider"></li>            </ul>          </li>        </ul>        <form class="d-flex" role="search" @submit.prevent="logout">          <button class="btn border border-white text-white" type="submit">Logout</button>        </form>      </div>    </div>  </nav></template><script>import axios from "../axios";import router from "../routes";  export default {    name: "Header",    data(){      return {      }    },    props: {      currentUser: {},    },    methods: {      logout(){        let url = 'http://localhost/api/logout';        axios.delete(url)            .then((response) => {              if(response.status === 200){                router.push({path: '/login'});              }            })            .catch((error) => console.error(error))      },    }  }</script><style scoped lang="sass"></style>