<template>  <Header :current-user="currentUser"/>  <div class="card mx-auto box shadow-lg" style="width: 30rem; margin-top: 150px">    <div :style="`background-image: url(http://localhost/imgs/${carData.image});`" id="card-image"></div>    <div class="card-body">      <h5 class="card-title">{{ carData.brand }}</h5>      <p class="card-text">{{ carData.model }}</p>      <p class="card-text">{{  carData.year }}</p>    </div>    <ul class="list-group list-group-flush">      <li class="list-group-item">{{  (carData.price / 100).toLocaleString('pt-br',{style: 'currency', currency: 'BRL'}) }}</li>      <li class="list-group-item" v-if="carData.price > carData.currentFipePrice && carData.currentFipePrice">{{(priceDifference / 100).toLocaleString('pt-br',{style: 'currency', currency: 'BRL'})}}+ according to the FIPE table</li>      <li class="list-group-item" v-else-if="carData.price < carData.currentFipePrice && carData.currentFipePrice">{{(priceDifference / 100).toLocaleString('pt-br',{style: 'currency', currency: 'BRL'})}} according to the FIPE table</li>      <li class="list-group-item" v-else-if="carData.currentFipePrice">Same value as FIPE table</li>      <li v-if="carData.hasAirBag == 1" class="list-group-item">Has Air Bag</li>      <li v-if="carData.hasInsurance == 1" class="list-group-item">Has Insurance</li>    </ul>  </div></template><script>import Header from "../components/Header.vue";import axios from '../axios';export default {  name: 'AboutCar',  components: {    Header,  },  data(){    return {      carData: null,      currentUser: null,      priceDifference: null,    }  },  beforeCreate() {    let url = 'http://localhost/api/user';    axios.get(url)        .then(response => this.currentUser = response.data)        .catch(error => window.location.replace('http://localhost:3000/login'))  },  beforeMount() {    let url = `http://localhost/api/about/${this.$route.params.carId}`    axios.get(url)        .then(response => {          this.carData = response.data.car          if(this.carData.currentFipePrice){            this.priceDifference = this.carData.price - this.carData.currentFipePrice;          }        })        .catch(error => console.log(error))  }}</script><style scoped>#card-image{  width: 100%;  height: 300px;  background-size: cover;  background-position: center;}</style>