<template>  <Header :current-user="currentUser"/>  <div class="dashboard-grid mt-5">    <div class="card shadow-sm" style="width: 300px">      <div class="card-body">        <span class="fw-bold">Total car value: </span>{{ totalCarValue }}      </div>    </div>    <div class="card" style="width: 300px">      <div class="card-body shadow-sm">        <span class="fw-bold">Cars: {{ cars.length }}</span>      </div>    </div>    <div class="card box shadow-sm" style="width: 300px">      <div class="card-body">        <span class="fw-bold">Users: </span>{{ users.length }}      </div>    </div>  </div>  <div>    <canvas id="myChart"></canvas>  </div></template><script>import Header from '../components/Header.vue';import axios from "../axios";export default {  name: "Dashboard",  components: {    Header,  },  data(){    return {      currentUser: null,      totalCarValue: null,      users: null,      cars: null,    }  },  beforeCreate() {    axios.get('http://localhost/api/user')        .then((response) => {          this.currentUser = response.data        })        .catch(error => {          window.location.replace('http://localhost:3000/login')        })  },  created() {    axios.get('http://localhost/api/dashboard')        .then(response   => {          this.totalCarValue = (response.data.total_price / 100).toLocaleString('pt-br',{style: 'currency', currency: 'BRL'})          this.users = response.data.users          this.cars = response.data.cars        }).catch(error => console.log(error))  }}</script><style scoped>  .dashboard-grid{    display: flex;    gap: 15px;    margin: 0 auto;    width: 90%;  }  .card{    transition: .6s;    cursor: pointer;  }  .card:hover{    transform: translateY(-5px);  }</style>