<template>  <form class="position-absolute top-50 start-50 translate-middle border border-1 px-5 py-4 rounded-2 box shadow-lg" @submit.prevent="formSubmit" style="width:500px">    <div class="mb-3">      <label for="exampleInputEmail1" class="form-label">Email address</label>      <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" v-model="loginCredentials.email">    </div>    <div class="mb-3">      <label for="exampleInputPassword1" class="form-label" >Password</label>      <input type="password" class="form-control" id="exampleInputPassword1" v-model="loginCredentials.password">    </div>    <div class="group-button d-flex">      <button type="submit" class="btn btn-primary">Login</button>      <router-link to="/register"><button type="button" class="btn btn-primary ms-2" >Register</button></router-link>    </div>    <div class="alert alert-danger mt-3" role="alert" v-if="errors">      {{ errors }}    </div>  </form></template><script>import router from "../routes";import axios from '../axios';export default {  name: "Login",  data(){    return {      loginCredentials: {        email: '',        password: '',      },      errors: null,    }  },  methods: {    formSubmit(){      let url = 'http://localhost/api/login';      axios.post(url, this.loginCredentials)          .then((response) => {            if(response.status == 200){              router.push({path: 'home'})            }          })          .catch((error) => {            this.errors = error.response.data.message          })    },    }}</script><style scoped>  .login-container{    position: absolute;    top: 50%;    left: 50%;    transform: translate(-50%, -50%);  }</style>