<template>  <Header :current-user="currentUser"/>  <div class="cars-wrapper mt-5">    <div v-for="car in cars" class="card box shadow-lg" style="width: 25rem;" id="car-card">      <div class="card-header" style="height: 40px; background: transparent">        <p>{{ car.user.name }}</p>      </div>      <div :style="`background-image: url(http://localhost/imgs/${car.image});`" id="card-image"></div>      <div class="card-body">        <h5 class="card-title">{{car.brand}}</h5>        <p class="card-text">{{car.model}}</p>        <p class="card-text">{{car.year}}</p>        <p class="card-text">{{ (car.price / 100).toLocaleString('pt-br',{style: 'currency', currency: 'BRL'})}}</p>        <router-link :to="{name: 'aboutCar', params: {carId: car.id}}"><a :class="`btn btn-primary ${car.user.id !== currentUser.id ? 'w-100' : ''}`">More</a></router-link>        <a v-if="car.user_id == currentUser.id" href="#" data-bs-toggle="modal" data-bs-target="#staticBackdrop" class="btn btn-primary mx-2" @click="deleteCar(car.id)">Delete</a>        <router-link v-if="car.user_id == currentUser.id"  :to="{name: 'editCar', params: {carId: car.id}}"><a href="#" class="btn btn-primary">Edit</a></router-link>      </div>    </div>  </div></template>x<script>import Header from '../components/Header.vue';import axios from "../axios";export default {  name: 'Profile',  components: {    Header,  },  data(){    return {      currentUser: null,      cars: [],    }  },  beforeCreate() {    axios.get('http://localhost/api/user')        .then((response) => {          this.currentUser = response.data        }).catch(() => {          window.location.replace('http://localhost:3000/login')        })  },  created() {    axios.get(`http://localhost/api/profile/${this.$route.params.userId}`)        .then(response => {          for(let car of response.data.cars){            for(let user of response.data.users){              if(car.user_id === user.id){                car.user = user;                this.cars.push(car);              }            }          }        }).catch( error => console.log(error))  }}</script><style scoped>.cars-wrapper{  display: grid;  width: 100%;  grid-template-columns: auto auto auto;  justify-content: center;  row-gap: 70px;  column-gap: 70px;}#car-card{  transition: .6s;}#car-card:hover{  transform: translateY(-7px);}#card-image{  width: 100%;  height: 300px;  background-size: cover;  background-position: center;}</style>