<template>  <form class="position-absolute top-50 start-50 translate-middle border border-1 px-5 py-4 rounded-2 box shadow-lg" @submit.prevent="formSubmit" style="width: 500px">    <div class="mb-3">      <label for="exampleInputEmail1" class="form-label">Name</label>      <input v-model="registerCredentials.name" type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">    </div>    <div class="mb-3">      <label for="exampleInputEmail1" class="form-label">Email address</label>      <input  v-model="registerCredentials.email" type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">    </div>    <div class="mb-3">      <label for="exampleInputPassword1" class="form-label" >Password</label>      <input  v-model="registerCredentials.password" type="password" class="form-control" id="exampleInputPassword1">    </div>    <div class="mb-3">      <label for="exampleInputPassword1" class="form-label" >Confirm password</label>      <input  v-model="registerCredentials.passwordConfirmation" type="password" class="form-control" id="exampleInputPassword1">    </div>    <div class="group-button d-flex">      <button type="submit" class="btn btn-primary">Register</button>      <router-link to="/login"><button type="button" class="btn btn-primary ms-2" >Login</button></router-link>    </div>      <div v-if="validators" v-for="validator in this.validators" class="alert alert-danger mt-3" role="alert">        {{ validator[0] }}      </div>  </form></template><script>  import axios from "../axios";  import router from "../routes";  export default {    name: "Register",    data(){      return {       registerCredentials: {         name: '',         email: '',         password: '',         passwordConfirmation: '',       },        validators: null,      }    },    methods: {      formSubmit(){        let url = 'http://localhost/api/register';        axios.post(url, this.registerCredentials)            .then((response) => {              if(response.status == 200){                router.push ({path: '/home'})              }            })              .catch((error) => this.validators = error.response.data.errors)      }    }  }</script><style></style>